INSERT INTO products (types_id, name, price, weight, description, metal_id) VALUES (3, 'ЦЕПЬ РУЧНОЙ РАБОТЫ ИЗ ЖЕЛТОГО ЗОЛОТА, ПЛЕТЕНИЕ БИСМАРК', 60990, 7, 'Плетение «Бисмарк»  - одно из наиболее сложных, но в то же время прочных плетений ручного производства. В нем все звенья состоят из нескольких колец, чем достигается красивая и прочная вязка.', 2);
